// <summary>Control for the option set editor.</summary>
namespace OptionSetEditor
{
    using System.ComponentModel.Composition;
    using XrmToolBox.Extensibility;
    using XrmToolBox.Extensibility.Interfaces;

    /// <summary>
    /// Option set editor class.
    /// </summary>
    [Export(typeof(IXrmToolBoxPlugin)),
    ExportMetadata("BackgroundColor", "White"),
    ExportMetadata("PrimaryFontColor", "Black"),
    ExportMetadata("SecondaryFontColor", "LightGray"),
    ExportMetadata("SmallImageBase64", ""),
    ExportMetadata("BigImageBase64", ""),
    ExportMetadata("Name", "Option Set Editor"),
    ExportMetadata("Description", "Allows quick and easy updating, exporting and importing of optionsets.")]
    public class XrmOptionSetEditor : PluginBase
    {
        /// <summary>
        /// Returns the control.
        /// </summary>
        /// <returns>The control.</returns>
        public override IXrmToolBoxPluginControl GetControl()
        {
            return new XrmOptionSetEditorControl();
        }
    }
}
